-- reset_sequences.sql
DO $$
DECLARE
  rec RECORD;
  t TEXT;
  c TEXT;
  seqname TEXT;
  seq_ident TEXT;
BEGIN
  FOR rec IN SELECT * FROM (VALUES
    ('Goals','id'),
    ('Tasks','id'),
    ('Activities','id'),
    ('Goals','rollNo'),
    ('Tasks','rollNo'),
    ('Activities','rollNo')
  ) AS v(table_name, col_name)
  LOOP
    t := rec.table_name;
    c := rec.col_name;

    -- try to get existing serial sequence (returns NULL if none)
    seqname := pg_get_serial_sequence(format('public."%s"', t), c);

    IF seqname IS NOT NULL THEN
      -- set sequence last_value = MAX(column) so nextval() => MAX+1
      EXECUTE format(
        'SELECT setval(%L, COALESCE((SELECT MAX(%I) FROM public."%s"), 0))',
        seqname, c, t
      );
      RAISE NOTICE 'Reset existing sequence % to max(%).', seqname, c;
    ELSE
      -- no attached sequence found: create one, set it to max, attach as default and own it
      seq_ident := quote_ident('public') || '.' || quote_ident(t || '_' || c || '_seq');
      EXECUTE format('CREATE SEQUENCE IF NOT EXISTS %s', seq_ident);
      EXECUTE format(
        'SELECT setval(%L, COALESCE((SELECT MAX(%I) FROM public."%s"), 0))',
        seq_ident, c, t
      );
      EXECUTE format('ALTER TABLE public."%s" ALTER COLUMN %I SET DEFAULT nextval(%L)', t, c, seq_ident);
      EXECUTE format('ALTER SEQUENCE %s OWNED BY public."%s".%I', seq_ident, t, c);
      RAISE NOTICE 'Created sequence % and attached to %.%.', seq_ident, t, c;
    END IF;
  END LOOP;
END;
$$ LANGUAGE plpgsql;


